const openAlert=document.querySelector("#openAlertBtn"),alertWindow=document.querySelector("#alertWindow"),clickCountDisplay=document.querySelector("#clickCount"),resetBtn=document.querySelector("#resetCountBtn"),closeBtn=document.querySelector("#closeButton");function refreshDisplay(){localStorage.hasOwnProperty("clickCount")?clickCountDisplay.innerHTML=localStorage.clickCount:clickCountDisplay.innerHTML=0}function closeAlert(){alertWindow.style.display="none"}openAlert.onclick=()=>{alertWindow.style.display="block",localStorage.hasOwnProperty("clickCount")?(localStorage.clickCount=Number(localStorage.clickCount)+1,5<=localStorage.clickCount&&(resetBtn.style.display="block")):localStorage.clickCount=1,refreshDisplay()},closeBtn.onclick=()=>{closeAlert()},window.onclick=e=>{e.target==alertWindow&&closeAlert()},resetBtn.onclick=()=>{localStorage.removeItem("clickCount"),resetBtn.style.display="none",refreshDisplay()};